// src/Pages/Home.js
import React from "react";

const Home = () => {
  return (
    <section className="bg-gradient-to-b from-green-50 via-green-100 to-green-50 min-h-screen flex flex-col items-center justify-center px-6">
      <div className="text-center max-w-4xl">

        {/* Heading */}
        <h1 className="text-5xl md:text-6xl font-extrabold text-green-900 mb-6 tracking-wide leading-tight">
          कृषिAI
          <span className="block text-3xl md:text-4xl font-semibold text-green-700 mt-2">
            स्मार्ट खेती का डिजिटल भविष्य
          </span>
        </h1>

        {/* Description */}
        <p className="text-lg md:text-xl text-green-800 mb-4 leading-relaxed">
          KrishiAI एक आधुनिक डिजिटल प्लेटफ़ॉर्म है जो किसानों को
          <span className="font-semibold text-green-900"> स्मार्ट कृषि समाधान</span>,
          <span className="font-semibold text-green-900"> AI-आधारित सलाह</span> और
          <span className="font-semibold text-green-900"> ग्रामीण बाज़ार से सीधा जुड़ाव</span>
          प्रदान करता है।
        </p>

        <p className="text-base md:text-lg text-green-700 mb-10 leading-relaxed">
          हमारा उद्देश्य किसानों को सही जानकारी, बेहतर निर्णय और आधुनिक तकनीक
          के माध्यम से सशक्त बनाना है — ताकि खेती अधिक लाभदायक, टिकाऊ और
          भविष्य के लिए तैयार हो सके।
        </p>

      </div>
    </section>
  );
};

export default Home;
