import React, { useState } from 'react'
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();


  const [currentState, setCurrentState] = useState('Login');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    rememberMe: false,
    showPassword: false
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    // Clear error for this field when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (currentState === 'Sign Up' && !formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (currentState === 'Sign Up') {
      if (!formData.confirmPassword) {
        newErrors.confirmPassword = 'Please confirm your password';
      } else if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = 'Passwords do not match';
      }
    }

    return newErrors;
  };

  const onSubmitHandler = async (event) => {
    event.preventDefault();
    
    const newErrors = validateForm();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setLoading(true);
    try {
      // Add your API call here
      console.log(`${currentState} attempted with:`, formData);
      
      // Simulate API call
      setTimeout(() => {
        alert(`${currentState} successful!`);

         if (currentState === "Login") {
    navigate("/welcome"); // or "/home"
  } else {
    navigate("/login"); // or "/dashboard" after signup
  }


        setFormData({
          name: '',
          email: '',
          password: '',
          confirmPassword: '',
          rememberMe: false,
          showPassword: false
        });
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Error:', error);
      setLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setFormData(prev => ({
      ...prev,
      showPassword: !prev.showPassword
    }));
  };



  return (
    <div className='min-h-screen bg-gradient-to-br from-green-50 via-white to-green-100 flex items-center justify-center px-4 py-12 relative overflow-hidden'>
      {/* Animated Background Blob */}
      <div className='absolute top-10 right-10 w-72 h-72 bg-green-200 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse'></div>
      <div className='absolute -bottom-8 -left-8 w-72 h-72 bg-green-300 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse'></div>

      {/* Main Card Container */}
      <div className='w-full max-w-md bg-white rounded-2xl shadow-2xl p-8 border border-green-100 relative z-10'>
        
        {/* Header with Logo Icon */}
        <div className='text-center mb-8'>
          <div className='flex justify-center mb-4'>
            <div className='inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br from-green-400 to-green-600 rounded-full shadow-lg'>
              <span className='text-2xl'>üåæ</span>
            </div>
          </div>
          <h1 className='text-4xl font-bold bg-gradient-to-r from-green-600 to-green-800 bg-clip-text text-transparent mb-2'>
            {currentState}
          </h1>
          <p className='text-gray-600 text-sm text-center'>
            {currentState === 'Login' 
              ? 'Welcome back to KrishiAI' 
              : 'Join our farming community'}
          </p>
        </div>

        {/* Form */}
        <form onSubmit={onSubmitHandler} className='space-y-4'>
          
          {/* Name Field - Only for Sign Up */}
          {currentState === 'Sign Up' && (
            <div className='animate-fadeIn'>
              <label className='block text-sm font-semibold text-gray-700 mb-2 text-center'>
                Full Name
              </label>
              <input
                type='text'
                name='name'
                value={formData.name}
                onChange={handleInputChange}
                placeholder='Enter your full name'
                className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-200 text-center ${
                  errors.name ? 'border-red-500 bg-red-50' : 'border-gray-200 hover:border-green-300'
                }`}
              />
              {errors.name && <p className='text-red-500 text-xs mt-1 font-medium text-center'>‚úó {errors.name}</p>}
            </div>
          )}

          {/* Email Field */}
          <div>
            <label className='block text-sm font-semibold text-gray-700 mb-2 text-center'>
              Email Address
            </label>
            <div className='relative'>
              <input
                type='email'
                name='email'
                value={formData.email}
                onChange={handleInputChange}
                placeholder='Enter your email'
                className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-200 pl-10 text-center ${
                  errors.email ? 'border-red-500 bg-red-50' : 'border-gray-200 hover:border-green-300'
                }`}
              />
              <span className='absolute left-3 top-3.5 text-gray-400'>‚úâÔ∏è</span>
            </div>
            {errors.email && <p className='text-red-500 text-xs mt-1 font-medium text-center'>‚úó {errors.email}</p>}
          </div>

          {/* Password Field */}
          <div>
            <label className='block text-sm font-semibold text-gray-700 mb-2 text-center'>
              Password
            </label>
            <div className='relative'>
              <input
                type={formData.showPassword ? 'text' : 'password'}
                name='password'
                value={formData.password}
                onChange={handleInputChange}
                placeholder='Enter your password'
                className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-200 pl-10 text-center ${
                  errors.password ? 'border-red-500 bg-red-50' : 'border-gray-200 hover:border-green-300'
                }`}
              />
              <span className='absolute left-3 top-3.5 text-gray-400'>üîí</span>
              <button
                type='button'
                onClick={togglePasswordVisibility}
                className='absolute right-3 top-3.5 text-gray-500 hover:text-gray-700 transition'
              >
                {formData.showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
            {errors.password && <p className='text-red-500 text-xs mt-1 font-medium text-center'>‚úó {errors.password}</p>}
          </div>

          {/* Confirm Password - Only for Sign Up */}
          {currentState === 'Sign Up' && (
            <div className='animate-fadeIn'>
              <label className='block text-sm font-semibold text-gray-700 mb-2 text-center'>
                Confirm Password
              </label>
              <div className='relative'>
                <input
                  type={formData.showPassword ? 'text' : 'password'}
                  name='confirmPassword'
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  placeholder='Confirm your password'
                  className={`w-full px-4 py-3 border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-200 pl-10 text-center ${
                    errors.confirmPassword ? 'border-red-500 bg-red-50' : 'border-gray-200 hover:border-green-300'
                  }`}
                />
                <span className='absolute left-3 top-3.5 text-gray-400'>üîí</span>
              </div>
              {errors.confirmPassword && <p className='text-red-500 text-xs mt-1 font-medium text-center'>‚úó {errors.confirmPassword}</p>}
            </div>
          )}

          {/* Remember Me & Forgot Password */}
          {currentState === 'Login' && (
            <div className='flex flex-col items-center gap-3 mt-4'>
              <label className='flex items-center text-sm text-gray-700 cursor-pointer hover:text-green-600 transition'>
                <input
                  type='checkbox'
                  name='rememberMe'
                  checked={formData.rememberMe}
                  onChange={handleInputChange}
                  className='w-4 h-4 text-green-600 border-gray-300 rounded focus:ring-2 focus:ring-green-500 cursor-pointer'
                />
                <span className='ml-2 font-medium'>Remember me</span>
              </label>
              <a href='#' className='text-sm text-green-600 hover:text-green-700 font-semibold transition'>
                Forgot Password?
              </a>
            </div>
          )}

          {/* Submit Button */}
          <button
            type='submit'
            disabled={loading}
            className='w-full bg-gradient-to-r from-green-500 to-green-600 text-white py-3 rounded-lg font-bold hover:shadow-lg hover:from-green-600 hover:to-green-700 transition duration-200 disabled:from-gray-400 disabled:to-gray-400 disabled:cursor-not-allowed mt-8 flex items-center justify-center gap-2'
          >
            {loading ? (
              <>
                <span className='animate-spin'>‚è≥</span>
                Processing...
              </>
            ) : (
              <>
                {currentState === 'Login' ? 'üîì Sign In' : '‚ú® Create Account'}
              </>
            )}
          </button>
        </form>

        {/* Divider */}
        <div className='relative my-6'>
          <div className='absolute inset-0 flex items-center'>
            <div className='w-full border-t border-gray-200'></div>
          </div>
          <div className='relative flex justify-center text-sm'>
            <span className='px-2 bg-white text-gray-500'>or</span>
          </div>
        </div>

        {/* Toggle Login/Sign Up */}
        <div className='text-center'>
          {currentState === 'Login' ? (
            <p className='text-gray-700 text-sm'>
              Don't have an account?{' '}
              <button
                onClick={() => {
                  setCurrentState('Sign Up');
                  setFormData({
                    name: '',
                    email: '',
                    password: '',
                    confirmPassword: '',
                    rememberMe: false,
                    showPassword: false
                  });
                  setErrors({});
                }}
                className='text-green-600 font-bold hover:text-green-700 cursor-pointer transition hover:underline'
              >
                Sign Up
              </button>
            </p>
          ) : (
            <p className='text-gray-700 text-sm'>
              Already have an account?{' '}
              <button
                onClick={() => {
                  setCurrentState('Login');
                  setFormData({
                    name: '',
                    email: '',
                    password: '',
                    confirmPassword: '',
                    rememberMe: false,
                    showPassword: false
                  });
                  setErrors({});
                }}
                className='text-green-600 font-bold hover:text-green-700 cursor-pointer transition hover:underline'
              >
                Login
              </button>
            </p>
          )}
        </div>

        {/* Footer Text */}
        <p className='text-center text-xs text-gray-500 mt-6'>
          By continuing, you agree to our Terms of Service and Privacy Policy
        </p>
      </div>

      <style>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .animate-fadeIn {
          animation: fadeIn 0.3s ease-in-out;
        }
      `}</style>
    </div>
  )
}

export default Login
